mapper.update:
update faq
faq_title = #{title}, faq_content = #{content}, faq_update_date = #{updateDate}, faq_delete_yn = #{deleteYn}, faq_category_id = #{category.id}, faq_update_by = #{updateByUser.id},
faq_delete_date = #{deleteDate}
where faq_id = #{id}

mapper.select:
select faq_id as id, faq_title as title, faq_content as content, faq_create_date as createDate, faq_update_date as updateDate, faq_delete_date as deleteDate, faq_delete_yn as deleteYn, faq_category_id as "category.id", faq_category_name as "category.name", faq_create_by as "createByUser.id", create_user_nickname as "createByUser.nickname", faq_update_by as "updateByUser.id", update_user_nickname as "updateByUser.nickname" from (select f.faq_id, f.faq_title, f.faq_content, f.faq_create_date, f.faq_update_date, f.faq_delete_date, f.faq_delete_yn, c.faq_category_name, f.faq_category_id, f.faq_create_by, u1.user_nickname as create_user_nickname, f.faq_update_by, u2.user_nickname as update_user_nickname, @rownum := @rownum + 1 as row_num from (select @rownum := 0) r, faq f left join faq_category c on f.faq_category_id = c.faq_category_id left join user u1 on f.faq_create_by = u1.user_id left join user u2 on f.faq_update_by = u2.user_id
and f.faq_category_id = #{filt}
and f.faq_delete_yn = 'N'
order by faq_id desc ) as banner where row_num between #{begin} and #{end}

select count(*) from faq
faq_category_id = #{filt}
and faq_delete_yn = 'N'
