mapper.select:
select * from (select A.product_id as id, A.product_name as name, A.product_content as content, A.product_status_yn as statusYn, A.product_create_date as createDate, A.product_update_date as updateDate, A.product_delete_date as deleteDate, A.product_delete_yn as deleteYn, A.product_price as price, A.product_discount as discount, A.category_id as categoryId, A.parents_product_id as parentsId, (select product_image_name from product_image x where x.product_id = A.product_id order by x.product_image_id asc limit 1) AS imageName, C.product_category_id as category_id, C.product_category_name as category_name from product A INNER JOIN product_category C ON C.product_category_id = A.category_id
AND A.product_name LIKE CONCAT(#{keyword}, '%')
AND A.product_id = #{keyword}
AND (A.product_id = #{keyword} OR A.product_name LIKE CONCAT(#{keyword}, '%'))
order by A.product_id ) SA LIMIT #{pageSize} OFFSET #{offset}

select id, content, rating, createDate, voteCount, nickname, email, imageName from (select r.product_review_id as id, r.product_review_content as content, r.product_review_rating as rating, r.product_review_create_date as createDate, r.product_review_vote_count as voteCount, u.user_nickname as nickname, u.user_email as email, r.product_review_image as imageName, @rownum := @rownum +1 as row_num from (select @rownum := 0) r, product_review r left join order_item i on r.order_item_id = i.order_item_id left join user u on r.user_id = u.user_id where i.product_id = #{productId} and r.product_review_delete_yn = 'N' order by
r.product_review_create_date desc
r.product_review_vote_count desc
r.product_review_create_date desc
) as productReview where row_num between #{begin} and #{end}

mapper.delete:
delete from
    product_review_vote
where
    product_review_id = #{review.id}
  and user_id = #{user.id}

mapper.update:
update product_review
set product_review_delete_yn = 'Y'
where
    product_review_id = #{id}
    and user_id = #{user.id}

update user
set user_point = user_point + #{point}
where
    user_id = #{userId}

update user
set user_point = user_point - #{point}
where
    user_id = #{userId}

mapper.select:
select count(ps.product_id)
from product_scrap ps
where ps.product_id = #{id}
